<?php namespace App\Models; use Illuminate\Database\Eloquent\Model; class Task extends Model { protected $fillable=["title","description","due_date","priority","assigned_to_user_id","assigned_to_rank","status","assigned_by"]; public function assigner(){return $this->belongsTo(User::class,"assigned_by");} public function assignee(){return $this->belongsTo(User::class,"assigned_to_user_id");} }
